import React, { useEffect, useState } from 'react'
import styles from './Persons.module.css'

// Hooks
import { useFetch } from '../../hooks/useFetch'

// Components
import Card from '../../components/Card/Card'
import PageNavigator from '../../components/PageNavigator/PageNavigator'

const Persons = () => {

  const [data, setData] = useState()
  const [teste, setTeste] = useState(false)
  const { fetchCharacters } = useFetch()

  useEffect(() => {
    async function getData() {
      setData(await fetchCharacters())
    }
    getData()

  }, [])

  function handleScroll(e) {
    let scroll = document.getElementById('container')
    console.log(scroll.current);
  }

  return (
    <>
      {setTimeout(() => {
        setTeste(!teste)
      }, 1000)}
      <div className={styles.pageNavigator}>
        <PageNavigator size={data && data.info} />
      </div>
      <div className={styles.container} id='conatiner' onScroll={(e) => handleScroll(e.target)}>
        {data && data.results.map((c, k) => (
          <Card character={c} key={k} />
        ))}
      </div>
    </>
  )
}

export default Persons
