const BASE_URL = 'https://rickandmortyapi.com/api'

export const fetchData = async (type, arg) => {
  console.log(`${BASE_URL}/${type}${arg && '?'+arg}`);
  const data = await fetch(`${BASE_URL}/${type}${arg && '?'+arg}`)
  return await data.json()
}
